    private void proceedButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_proceedButton1ActionPerformed
        float balanceAmountFromDatabase, amount;
        try {
            amount = Float.parseFloat(amountField.getText());
            Class.forName("com.mysql.jdbc.Driver");
            Connection con = DriverManager.getConnection("jdbc:mysql://localhost/atm", "root", "Kishan@1072");
            java.util.Date date = new java.util.Date();
            java.sql.Date sqlDate = new java.sql.Date(date.getTime());
            java.sql.Timestamp sqlTime = new java.sql.Timestamp(date.getTime());
            String Query = "Select * from userInfo where name = '" + user + "';";
            Statement st = con.createStatement();
            ResultSet rs = st.executeQuery(Query);
            rs.next();
            balanceAmountFromDatabase = rs.getFloat(4);
            
            if(amount <= balanceAmountFromDatabase) {
                
                PreparedStatement ps = con.prepareStatement("insert into " + user + " values(?,?, + " + amount +");");
                ps.setDate(1, sqlDate);
                ps.setTimestamp(2, sqlTime);
                ps.executeUpdate();
                
                String Query2;
                balanceAmountFromDatabase -= amount;
                Query2 = "Update userInfo set balanceAmount = " + balanceAmountFromDatabase + " where name = '" + user +"';";
                Statement stmt = con.createStatement();
                stmt.executeUpdate(Query2);
                
                JOptionPane.showMessageDialog(this, "<html>Tansaction Successfully Completed<br/><b>Please collect the amount before leaving</b></html>", "Success!", JOptionPane.INFORMATION_MESSAGE);
                jTabbedPane1.setSelectedIndex(0);
                ps.close();
            }
            else if(balanceAmountFromDatabase == 0 || amount > balanceAmountFromDatabase) {
                JOptionPane.showMessageDialog(this, "The account does not have sufficient balance!", "Insufficient balance!", JOptionPane.ERROR_MESSAGE);
                amountField.setText("");
            }
            con.close();
            amountField.setText("");
        }
        catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Please enter a valid amount");
            amountField.setText("");
        }
    }//GEN-LAST:event_proceedButton1ActionPerformed

