    private void proceedButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_proceedButton2ActionPerformed
        receiverUserName = userNameField.getText();
        String amountEntered = amountField2.getText();
        float balanceAmountFromDatabase;
        float amountEnteredFloat = Float.parseFloat(amountEntered);
        try {
            Class.forName("com.mysql.jdbc.Driver");
            Connection con = DriverManager.getConnection("jdbc:mysql://localhost/atm", "root", "Kishan@1072");
            
            java.util.Date date = new java.util.Date();
            java.sql.Date sqlDate = new java.sql.Date(date.getTime());
            java.sql.Timestamp sqlTime = new java.sql.Timestamp(date.getTime());
            
            String Query = "Select * from userInfo where name = '" + user + "';";
            Statement st = con.createStatement();
            ResultSet rs = st.executeQuery(Query);
            rs.next();
            balanceAmountFromDatabase = rs.getFloat(4);
            
            String Query1 = "Select * from userInfo where name = '" + receiverUserName + "';";
            Statement st1 = con.createStatement();
            ResultSet rs2 = st1.executeQuery(Query1);
            rs2.next();
            receiverBalance = rs2.getFloat(4);
            
            if(balanceAmountFromDatabase >= amountEnteredFloat) {
                balanceAmountFromDatabase -= amountEnteredFloat;
                receiverBalance += amountEnteredFloat;
                String Query2 = "Update userInfo set balanceAmount = " + balanceAmountFromDatabase + " where name = '" + user + "';";
                String Query3 = "Update userInfo set balanceAmount = " + receiverBalance + " where name = '" + receiverUserName + "';";
                st.executeUpdate(Query2);
                st.executeUpdate(Query3);
                userNameField.setText("");
                amountField2.setText("");
                JOptionPane.showMessageDialog(this, "The Transaction is completed successfully", "Success", JOptionPane.INFORMATION_MESSAGE);
                
                PreparedStatement ps = con.prepareStatement("insert into " + user + " values(?,?," + amountEnteredFloat + ");");
                ps.setDate(1, sqlDate);
                ps.setTimestamp(2, sqlTime);
                ps.executeUpdate();
            }
            else {
                JOptionPane.showMessageDialog(this, "The account does not have sufficient balance!", "Insufficient balance!", JOptionPane.ERROR_MESSAGE);
                amountField2.setText("");
            }
        }  
        catch(Exception e) {
            JOptionPane.showMessageDialog(this, "The User Name or amount entred is Invalid", "Invalid details", JOptionPane.ERROR_MESSAGE);
            userNameField.setText("");
            amountField2.setText("");
        }
    }//GEN-LAST:event_proceedButton2ActionPerformed

